package com.ecommerce.controllers\n\nimport com.ecommerce.models.Email\nimport com.ecommerce.services.EmailService\nimport org.springframework.http.ResponseEntity\nimport org.springframework.web.bind.annotation.PostMapping\nimport org.springframework.web.bind.annotation.RequestBody\nimport org.springframework.web.bind.annotation.RequestMapping\nimport org.springframework.web.bind.annotation.RestController\n\n@RestController\n@RequestMapping(\"/email\")\nclass EmailController(private val emailService: EmailService) {\n\n    @PostMapping(\"/send\")\n    fun sendEmail(@RequestBody email: Email): ResponseEntity<String> {\n        return try {\n            emailService.sendEmail(email)\n            ResponseEntity.ok(\"Email sent successfully\")\n        } catch (e: Exception) {\n            ResponseEntity.status(500).body(\"Error sending email: \${e.message}\")\n        }\n    }\n}